<?php namespace App\Http\Controllers\Frontend;use App\Models\Contact;use Carbon\Carbon;use Illuminate\Http\Request;use App\Http\Controllers\Controller;class ContactController extends Controller{public function contact(){return view('frontend.contact');}public function sendContact(Request $request){$data=$request->except('_token');$data['created_at']=$data['updated_at']=Carbon::now();$id=Contact::insert($data);if($id>0){return redirect()->back()->with('success',' The update was successful !!! ');}return redirect()->back()->with('danger',' The update was failed !!! ');}}